UPDATE CORE_AJUDA_TELA
   SET OBJETIVO = 'CONFIGURAR OS DADOS CADASTRAIS E DE REMUNERACAO DOS FUNCIONARIOS.',
       QUANDO_UTILIZAR = 'UTILIZAR SEMPRE QUE FOR NECESSARIO INCLUIR, AJUSTAR OU CONSULTAR OS DADOS DOS FUNCIONARIOS, INCLUSIVE JORNADA DE TRABALHO E INFORMACOES DE SALARIO.',
       DESCRICAO = 'TELA RESPONSAVEL PELO CADASTRO DOS FUNCIONARIOS DA EMPRESA. PERMITE INFORMAR DADOS BASICOS (NOME, DOCUMENTOS, DEPARTAMENTO, JORNADA DE TRABALHO) E OS DADOS DE REMUNERACAO (TIPO DE SALARIO, SALARIO BASE E CARGA HORARIA MENSAL DE REFERENCIA). ESTES CAMPOS SERAO UTILIZADOS POSTERIORMENTE PARA EMISSAO DO CARTAO PONTO E CALCULO DE HORAS EXTRAS.',
       PASSO_A_PASSO = '1) INFORMAR OS DADOS BASICOS DO FUNCIONARIO (NOME, DOCUMENTOS, CONTATO).\n2) SELECIONAR O DEPARTAMENTO E, SE APLICAVEL, A JORNADA DE TRABALHO PADRAO.\n3) NA SECAO DE REMUNERACAO, DEFINIR O TIPO DE SALARIO (ATUALMENTE MENSALISTA), INFORMAR O SALARIO BASE E A CARGA HORARIA MENSAL DE REFERENCIA (EX.: 220 HORAS).\n4) MARCAR SE O FUNCIONARIO ESTA ATIVO OU INATIVO.\n5) CLICAR EM SALVAR PARA GRAVAR AS INFORMACOES.\n6) UTILIZAR ESTA TELA SEMPRE QUE PRECISAR ATUALIZAR DADOS CADASTRAIS OU DE REMUNERACAO DO FUNCIONARIO.'
 WHERE CODIGO_TELA = 'CAD005_RH_FUNCIONARIO';
